import React, { useState, useEffect } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';

const AdForm = ({ categories, onSubmit, loading, initialData }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [subcategoryId, setSubcategoryId] = useState('');
  const [images, setImages] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    if (initialData) {
      setTitle(initialData.title);
      setDescription(initialData.description);
      setSubcategoryId(initialData.subcategory_id);
    }
  }, [initialData]);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!title || !description || !subcategoryId) {
      return setError('Please fill in all required fields');
    }

    setError('');

    onSubmit({
      title,
      description,
      subcategory_id: parseInt(subcategoryId)
    }, images);
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);

    if (files.length > 7) {
      setError('You can upload a maximum of 7 images');
      return;
    }

    setImages(files);
    setError('');
  };

  return (
    <Form onSubmit={handleSubmit}>
      {error && <Alert variant="danger">{error}</Alert>}

      <Form.Group className="mb-3" controlId="title">
        <Form.Label>Title</Form.Label>
        <Form.Control
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="description">
        <Form.Label>Description</Form.Label>
        <Form.Control
          as="textarea"
          rows={5}
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="category">
        <Form.Label>Category</Form.Label>
        <Form.Select
          value={subcategoryId}
          onChange={(e) => setSubcategoryId(e.target.value)}
          required
        >
          <option value="">Select a category</option>
          {categories.map(category => (
            <optgroup key={category.id} label={category.name}>
              {category.subcategories?.map(subcategory => (
                <option key={subcategory.id} value={subcategory.id}>
                  {subcategory.name}
                </option>
              ))}
            </optgroup>
          ))}
        </Form.Select>
      </Form.Group>

      {!initialData && (
        <Form.Group className="mb-3" controlId="images">
          <Form.Label>Images (up to 7)</Form.Label>
          <Form.Control
            type="file"
            multiple
            accept="image/*"
            onChange={handleImageChange}
          />
          <Form.Text className="text-muted">
            You can select multiple images (max 7)
          </Form.Text>
        </Form.Group>
      )}

      <Button disabled={loading} type="submit">
        {initialData ? 'Update Ad' : 'Create Ad'}
      </Button>
    </Form>
  );
};

export default AdForm;
