import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { Container, Row, Col, Card, Button, Alert } from 'react-bootstrap';
import { adsService } from '../services/ads';
import ImageGallery from '../components/ui/ImageGallery';

const AdDetail = () => {
  const { id } = useParams();
  const [ad, setAd] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    loadAd();
  }, [id]);

  const loadAd = async () => {
    try {
      setLoading(true);
      const data = await adsService.getById(id);
      setAd(data);
    } catch (error) {
      setError('Ошибка загрузки объявления');
      console.error('Ошибка загрузки объявления:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <Container className="text-center">Loading...</Container>;
  if (error) return <Container><Alert variant="danger">{error}</Alert></Container>;
  if (!ad) return <Container><Alert variant="warning">Ad not found</Alert></Container>;

  return (
    <Container>
      <Row>
        <Col md={8}>
          <h1>{ad.title}</h1>
          <p className="text-muted">
            Category: {ad.subcategory?.name} • 
            Posted by: {ad.user?.username} • 
            {new Date(ad.created_at).toLocaleDateString()}
          </p>

          {ad.images && ad.images.length > 0 && (
            <ImageGallery images={ad.images} />
          )}

          <Card className="mt-4">
            <Card.Body>
              <Card.Title>Description</Card.Title>
              <Card.Text>{ad.description}</Card.Text>
            </Card.Body>
          </Card>
        </Col>

        <Col md={4}>
          <Card>
            <Card.Header>Contact Information</Card.Header>
            <Card.Body>
              <p><strong>Username:</strong> {ad.user?.username}</p>
              <p><strong>Phone:</strong> {ad.user?.phone}</p>

              {ad.user?.social_network && ad.user?.social_contact && (
                <p>
                  <strong>{ad.user.social_network}:</strong> {ad.user.social_contact}
                </p>
              )}

              <div className="d-grid gap-2">
                <Button variant="outline-primary">
                  Show Phone Number
                </Button>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default AdDetail;
