import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Alert, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { adsService } from '../services/ads';
import AdCard from '../components/ui/AdCard';
import Pagination from '../components/common/Pagination';

const MyAds = () => {
  const [ads, setAds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    loadAds();
  }, [currentPage]);

  const loadAds = async () => {
    try {
      setLoading(true);
      const data = await adsService.getUserAds(currentPage);
      setAds(data.ads);
      setTotalPages(Math.ceil(data.total / data.limit));
    } catch (error) {
      setError('Failed to load your ads');
      console.error('Error loading ads:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this ad?')) {
      try {
        await adsService.delete(id);
        loadAds(); // Reload ads after deletion
      } catch (error) {
        setError('Failed to delete ad');
        console.error('Error deleting ad:', error);
      }
    }
  };

  if (loading) return <Container className="text-center">Loading...</Container>;

  return (
    <Container>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>My Ads</h1>
        <Link to="/create-ad">
          <Button variant="primary">Create New Ad</Button>
        </Link>
      </div>

      {error && <Alert variant="danger">{error}</Alert>}

      {ads.length === 0 ? (
        <div className="text-center">
          <h4>You don't have any ads yet</h4>
          <Link to="/create-ad">
            <Button variant="primary" className="mt-3">Create Your First Ad</Button>
          </Link>
        </div>
      ) : (
        <>
          <Row>
            {ads.map(ad => (
              <Col key={ad.id} md={6} lg={4} className="mb-4">
                <AdCard ad={ad} showActions onDelete={() => handleDelete(ad.id)} />
              </Col>
            ))}
          </Row>

          {totalPages > 1 && (
            <Pagination
              currentPage={currentPage}
              totalPages={totalPages}
              onPageChange={setCurrentPage}
            />
          )}
        </>
      )}
    </Container>
  );
};

export default MyAds;
